import React, { useState } from "react";
import axios from "axios";
import "./SetThreshold.css";

const SetThreshold = () => {
  const [nodeId, setNodeId] = useState("");
  const [threshold, setThreshold] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setError("");

    // Validation
    if (!nodeId || !threshold) {
      setError("Both Node ID and Threshold are required.");
      return;
    }
    if (!["1", "2"].includes(nodeId)) {
      setError("Node ID must be 1 or 2.");
      return;
    }
    if (isNaN(threshold) || parseInt(threshold, 10) < 0) {
      setError("Threshold must be a positive number.");
      return;
    }

    try {
      const response = await axios.post(
        `https://smart-farm-v14p.onrender.com/set_threshold/${nodeId}`,
        { threshold: parseInt(threshold, 10) }
      );

      if (response.status === 200 && response.data.status === "success") {
        setMessage(`Threshold set successfully for Node ${nodeId}`);
      } else {
        setError(response.data.message || "Failed to set threshold.");
      }
    } catch (err) {
      setError(`Error setting threshold: ${err.message}`);
    }
  };

  return (
    <div className="set-threshold">
      <h2>Set Moisture Threshold</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="nodeId">Node ID:</label>
          <input
            type="text"
            id="nodeId"
            value={nodeId}
            onChange={(e) => setNodeId(e.target.value)}
            placeholder="Enter Node ID (1 or 2)"
          />
        </div>
        <div className="form-group">
          <label htmlFor="threshold">Threshold:</label>
          <input
            type="number"
            id="threshold"
            value={threshold}
            onChange={(e) => setThreshold(e.target.value)}
            placeholder="Enter Threshold Value"
          />
        </div>
        <button type="submit">Set Threshold</button>
      </form>
      {message && <p className="success">{message}</p>}
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default SetThreshold;
